import { useEffect, useState } from "react";
import ToDoList from "./ToDoList";


function App() {

  const URL = 'http://localhost:8081/todoes'

  const [toDoes, setToDoes] = useState([])
  const [fetchError, setFetchError] = useState(null)
  const [isLoading, setIsLoading] = useState(true)

  useEffect(() => {
    const fetchToDoes = async () => {
    fetch(URL)
    .then(response => response.json())
    .then(data => setToDoes(data))
    .catch((err) => setFetchError(err))
    setIsLoading(false)

  }
  setTimeout( () => {
    (async () => await fetchToDoes())();
  }, 1000)
}, [])

let todoMap = []

if (toDoes[0] !== undefined) {
  for (let i = 0; i < toDoes.length; i++) {
    let o = {
      "todo_id": toDoes[i].todo_id,
      "todo_name": toDoes[i].todo_name,
      "todo_complexity": toDoes[i].todo_complexity,
      "todo_status": toDoes[i].todo_status,
      "user_id": toDoes[i].user_id,
    }
    todoMap += o
  }
  console.log(todoMap)
}




  return (
    <div className="App">
      {!fetchError &&
      <div>
        <ToDoList toDoes={toDoes}/>
      </div>
      }

    </div>
  );
}

export default App;
