import React from 'react'
import ToDo from './ToDo'

const ToDoList = ({toDoes, options}) => {

    let completedTodoes = []
    let activeTodoes = []

    if (toDoes[0] !== undefined) {

    for (let i = 0; i < toDoes.length; i++) {
        if (toDoes[i].todo_status === false) {
            activeTodoes.push(toDoes[i])
        } else {
            completedTodoes.push(toDoes[i])
        }
    }
    }

    const resultMap = []
    if (options === "Active") {
        resultMap = activeTodoes
    } else if (options === "Completed") {
        resultMap = completedTodoes
    }

    if (toDoes[0] !== undefined) {
  return (
    <ul className='toDoList'>
        {resultMap.map((Todo) => (
            <ToDo 
            key={Todo.todo_id}
            Todo={Todo}
            options={options}/>
        ))}
    </ul>
  )
}
}

export default ToDoList
