import { useEffect, useState } from "react";
import ToDoList from "./ToDoList";
import Nav from "./Nav";
import CreateToDo from "./CreateToDo";
import APIRequest from "./APIrequest";


function App() {

  const URL = 'http://localhost:8081/todoes'

  const [toDoes, setToDoes] = useState([])
  const [fetchError, setFetchError] = useState(null)
  const [isLoading, setIsLoading] = useState(true)
  const [options, setOptions] = useState("Active")
  const [difficulty, setDifficulty] = useState("Easy")
  const [newItem, setNewItem] = useState("")
  const [event, setEvent] = useState("")

  useEffect(() => {
    const fetchToDoes = async () => {
    fetch(URL)
    .then(response => response.json())
    .then(data => setToDoes(data))
    .catch((err) => setFetchError(err))
    setIsLoading(false)

  }
  setTimeout( () => {
    (async () => await fetchToDoes())();
  }, 1000)
}, [event])

const AddToDo = async (todo_name, difficulty) => {
  let todo_complexity
  let user_id = 1 // THIS WILL BE REPLACED WITH USER REGISTRATION AND LOG IN LOGIC
  switch (difficulty) {
    case "Easy":
    todo_complexity = 1
    break
    case "Medium":
    todo_complexity = 2
    break
    case "Hard":
    todo_complexity = 3
    break
    default:
    break
  }
  const NewToDo = {todo_name, todo_complexity, user_id}
  const postOptions = {
    method: 'PUT',
    headers: {
      'Content-Type': 'application/json'
    },
    body: JSON.stringify(NewToDo)
  }
  const result = await APIRequest(URL, postOptions)
  if (result) setFetchError(result)
  setEvent("")
}

const handleSubmit = (e) => {
  e.preventDefault()
  if (!newItem) return
  AddToDo(newItem, difficulty)
  setNewItem('')
  setDifficulty("Easy")
}

  return (
    <div className="App">
      <main>
        <Nav options={options} setOptions={setOptions}/>
        {isLoading && <h1>Loading todoes</h1>}
        {!fetchError && !isLoading && options !== "Create" &&
        <div>
          <ToDoList toDoes={toDoes} options={options}/>
        </div>
        }
        {!fetchError && !isLoading && options === "Create" &&
        <div>
          <CreateToDo handleSubmit={handleSubmit} setDifficulty={setDifficulty} newItem={newItem} setNewItem={setNewItem}/>
        </div>
        }
      </main>
    </div>
  );
}

export default App;
