import { useEffect, useState } from "react";
import ToDoList from "./ToDoList";
import Nav from "./Nav";
import CreateToDo from "./CreateToDo";
import APIRequest from "./APIrequest";


function App() {

  const URL = 'http://localhost:8081/todoes'

  const [toDoes, setToDoes] = useState([])
  const [fetchError, setFetchError] = useState(null)
  const [isLoading, setIsLoading] = useState(true)
  const [options, setOptions] = useState("Active")
  const [difficulty, setDifficulty] = useState("Easy")
  const [newItem, setNewItem] = useState("")

  useEffect(() => {
    const fetchToDoes = async () => {
    fetch(URL)
    .then(response => response.json())
    .then(data => setToDoes(data))
    .catch((err) => setFetchError(err))
    setIsLoading(false)

  }
  setTimeout( () => {
    (async () => await fetchToDoes())();
  }, 1000)
}, [])

const AddToDo = async (todo_name, difficulty) => {
  let d 
  let userID = 1 // THIS WILL BE REPLACED WITH USER REGISTRATION AND LOG IN LOGIC
  switch (difficulty) {
    case "Easy":
    d = 1
    break
    case "Medium":
    d = 2
    break
    case "Hard":
    d = 3
    break
  }
  const NewToDo = {todo_name, d, checked: false, userID}
  const postOptions = {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json'
    },
    body: JSON.stringify(NewToDo)
  }
}

const handleSubmit = (e) => {
  e.preventDefault()
  if (!newItem) return
  AddToDo(newItem, difficulty)
  setNewItem('')
  setDifficulty("Easy")
}

